sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	// Process NeedIt Task Post Request
	var requestBody = request.body;
	var requestData = requestBody.data;

	if(requestData instanceof Array)
	{
		//test for multiple body objects / JSON To-do loop through incoming object?
		u_requested_for = requestData[0].u_requested_for;
		u_requested_for_email = requestData[0].u_requested_for_email;
		u_request_type = requestData[0].u_request_type;
		u_what_needed = requestData[0].u_what_needed;
		u_when_needed = requestData[0].u_when_needed;
		short_description = requestData[0].short_description;

	}
	else
	{
		u_requested_for = requestData.u_requested_for;
		u_requested_for_email = requestData.u_requested_for_email;
		u_request_type = requestData.u_request_type;
		u_what_needed = requestData.u_what_needed;
		u_when_needed = requestData.u_when_needed;	
		short_description = requestData.short_description;
	}



	//Create Needit Task
	var needitSysID = '';
	try
	{	
		var needit = new GlideRecord('x_58872_needit_needit');
		needit.initialize(); 
		needit.u_requested_for = u_requested_for; 
		needit.u_requested_for_email = u_requested_for_email; 
		needit.u_request_type = u_request_type; 
		needit.u_what_needed = u_what_needed; 
		needit.u_when_needed = u_when_needed; 
		needit.short_description = short_description; 

		//do it	and grab sys_id
		needitSysID = needit.insert();
	} 
	catch(e)
	{
		var error =  new sn_ws_err.ServiceError();
		error.setStatus(500);
		error.setDetail("Create Record Failed");
		error.setMessage("Failed to create:" + short_description);

		gs.log("Failed to create Needit Task: " + e.message);

		return error;
	}

	//Loookup newely created Needit Task so we can send it back in the response
	var taskNumber = '';
	try
	{
		var grlu = new GlideRecord('x_58872_needit_needit');
		grlu.initialize();
		grlu.addQuery('sys_id','=',needitSysID);
		grlu.query();

		if(grlu.next())
		{
			var taskNum = {"NeedIt Task: " : grlu.number};
			return taskNum;
		}
		else
		{
			response.body('A New Needit Task, number ' + taskNum + ' has been created');
			response.setStatus(201);
			return needitSysID;
		}
	}
	catch(e)
	{
		var error2 =  new sn_ws_err.ServiceError();
		error2.setStatus(500);
		error2.setDetail("Failed to find newely created Needit Task");

		gs.log("Failed to find sys_id: " + needitSysID + " : " + e.message);

		return error;
	}


})(request, response);
